INSERT INTO `bc_rencai_data` VALUES('1','<p><span style=\"font-family: 宋体; font-size: 12px;\">岗位职责：</span></p><p><span style=\"font-family: 宋体; font-size: 12px;\"><span style=\"font-size: 12px; font-family: Calibri;\">1</span>、负责项目开发，包括立项、评估、技术流程研发、结项等事项；</span></p><p><span style=\"font-family: 宋体; font-size: 12px;\"><span style=\"font-size: 12px; font-family: Calibri;\">2</span>、实时跟进相关专业领域的最新文献，实时洞察市场动向；</span></p><p><span style=\"font-family: 宋体; font-size: 12px;\"><span style=\"font-size: 12px; font-family: Calibri;\">3</span>、完成上级安排的其他相关工作。</span></p><p><span style=\"font-family: 宋体; font-size: 12px;\"><br/></span></p><p><span style=\"font-family: 宋体; font-size: 12px;\">任职要求：</span></p><p><span style=\"font-family: 宋体; font-size: 12px;\"><span style=\"font-size: 12px; font-family: Calibri;\">1</span>、生物技术、医学检验、免疫学、药学、微生物学等相关专业本科及以上学历（可接受优秀应届毕业生）；</span></p><p><span style=\"font-family: 宋体; font-size: 12px;\"><span style=\"font-size: 12px; font-family: Calibri;\">2</span>、从事试剂研发相关工作三年以上经历； </span></p><p><span style=\"font-family: 宋体; font-size: 12px;\"><span style=\"font-size: 12px; font-family: Calibri;\">3</span>、英语六级以上，可自主查阅海外文献；</span></p><p><span style=\"font-family: 宋体; font-size: 12px;\"><span style=\"font-size: 12px; font-family: Calibri;\">4</span>、工作积极主动、责任心强、具有一定的分析解决问题能力，良好的沟通表达能力和团队合作精神。</span></p><p><br/></p><p><span style=\"font-size: 12px;\">联系人：张丹</span></p><p><span style=\"font-size: 12px;\">Email：zhangdan@innovita.com.cn</span></p><p><span style=\"font-size: 12px;\">电话：010-83682249-8042</span></p>','0','','0','0','','0','1','','','');
INSERT INTO `bc_rencai_data` VALUES('2','<p><span style=\"font-family: 宋体; font-size: 12px;\">岗位职责：</span></p><p><span style=\"font-family: 宋体; font-size: 12px;\"><span style=\"font-size: 12px; font-family: Calibri;\">1</span>、掌握相关实验技术，如胶体金、酶联免疫、化学发光等； </span></p><p><span style=\"font-family: 宋体; font-size: 12px;\"><span style=\"font-size: 12px; font-family: Calibri;\">2</span>、按时准确进行实验数据的记录和整理，及时反映实验中出现的问题，撰写实验进度报告； </span></p><p><span style=\"font-family: 宋体; font-size: 12px;\"><span style=\"font-size: 12px; font-family: Calibri;\">3</span>、掌握生物实验室各种常见仪器的使用和管理，常用试剂的配置；</span></p><p><span style=\"font-family: 宋体; font-size: 12px;\"><span style=\"font-size: 12px; font-family: Calibri;\">4</span>、完成上级安排的其他相关工作。 </span></p><p><span style=\"font-family: 宋体; font-size: 12px;\"><br/></span></p><p><span style=\"font-family: 宋体; font-size: 12px;\">任职要求：</span></p><p><span style=\"font-family: 宋体; font-size: 12px;\"><span style=\"font-size: 12px; font-family: Calibri;\">1</span>、细胞生物学、生物化学、分子生物学、免疫学，检验医学、临床医学等相关专业本科及以上学历<span style=\"font-size: 12px; font-family: Calibri;\">(</span>可接受优秀应届毕业生<span style=\"font-size: 12px; font-family: Calibri;\">)</span>；</span></p><p><span style=\"font-family: 宋体; font-size: 12px;\"><span style=\"font-size: 12px; font-family: Calibri;\">2</span>、一年以上体外诊断试剂研发相关实验操作和理论知识；</span></p><p><span style=\"font-family: 宋体; font-size: 12px;\"><span style=\"font-size: 12px; font-family: Calibri;\">3</span>、接受短期异地轮岗。</span></p><p><span style=\"font-family: 宋体; font-size: 12px;\">&nbsp;</span></p><p><span style=\"font-size: 12px;\">联系人：张丹</span></p><p><span style=\"font-size: 12px;\">电话：010-83682249-8042</span></p><p><span style=\"font-size: 12px;\">Email：zhangdan@innovita.com.cn</span></p>','0','','0','0','','0','1','','','');
INSERT INTO `bc_rencai_data` VALUES('3','<p><span style=\"font-family: Calibri; font-size: 12px;\"><span style=\"font-family: 宋体;\">职位描述</span> ：</span></p><p><span style=\"font-family: Calibri; font-size: 12px;\">1<span style=\"font-size: 12px; font-family: 宋体;\">、根据公司业务范围负责区域渠道开发，收集整理客户信息，进行业务拓展业务接洽； </span></span></p><p><span style=\"font-family: Calibri; font-size: 12px;\">2<span style=\"font-size: 12px; font-family: 宋体;\">、负责跟客户沟通，促成项目订单，完成公司制定的销售业务； </span></span></p><p><span style=\"font-family: Calibri; font-size: 12px;\">3<span style=\"font-size: 12px; font-family: 宋体;\">、配合市场部完成公司产品培训及宣讲； </span></span></p><p><span style=\"font-family: Calibri; font-size: 12px;\">4<span style=\"font-size: 12px; font-family: 宋体;\">、定期向大区经理汇报工作进展，并进行业务分析等。 </span></span></p><p><span style=\"font-family: Calibri; font-size: 12px;\"><span style=\"font-size: 12px; font-family: 宋体;\"><br/></span></span></p><p><span style=\"font-family: Calibri; font-size: 12px;\">任职要求：</span></p><p><span style=\"font-family: Calibri; font-size: 12px;\">1<span style=\"font-size: 12px; font-family: 宋体;\">、生物技术、市场营销、医学检验、免疫学等相关专业专科及以上学历； </span></span></p><p><span style=\"font-family: Calibri; font-size: 12px;\">2<span style=\"font-size: 12px; font-family: 宋体;\">、具有两年以上生物行业销售经验，有体外诊断试剂技术或销售经验者优先； </span></span></p><p><span style=\"font-family: Calibri; font-size: 12px;\">3<span style=\"font-size: 12px; font-family: 宋体;\">、热爱销售工作，执行力强，具有较强的沟通能力，工作积极具有团队精神，具有良好的应变能力及抗压力。</span></span></p><p><span style=\"font-family: Calibri; font-size: 12px;\"><br/></span></p><p><span style=\"font-size: 12px;\">联系人：张丹</span></p><p><span style=\"font-size: 12px;\">电话：010-83682249-8042</span></p><p><span style=\"font-size: 12px;\">Email：zhangdan@innovita.com.cn</span></p>','0','','0','0','','0','1','','','');
INSERT INTO `bc_rencai_data` VALUES('4','<p><span style=\"font-family: Calibri; font-size: 12px;\">职位描述：</span></p><p><span style=\"font-family: Calibri; font-size: 12px;\">1<span style=\"font-size: 12px; font-family: 宋体;\">、制定产品市场推广策略，规划市场推广、活动方向和实施方案； </span></span></p><p><span style=\"font-family: Calibri; font-size: 12px;\">2<span style=\"font-size: 12px; font-family: 宋体;\">、制定学术推广会议计划</span>,<span style=\"font-size: 12px; font-family: 宋体;\">组织和实施各级临床学术推广活动； &nbsp;</span></span></p><p><span style=\"font-family: Calibri; font-size: 12px;\">3<span style=\"font-size: 12px; font-family: 宋体;\">、建立和维护产品相关专家网络，通过与专家学术合作，获得专家学术支持。 </span></span></p><p><span style=\"font-family: Calibri; font-size: 12px;\">4<span style=\"font-size: 12px; font-family: 宋体;\">、对营销人员进行产品、学术及市场专业知识培训和指导。 &nbsp;</span></span></p><p><span style=\"font-family: Calibri; font-size: 12px;\"><span style=\"font-size: 12px; font-family: 宋体;\"><br/></span></span></p><p><span style=\"font-family: Calibri; font-size: 12px;\">任职资格：</span></p><p><span style=\"font-family: Calibri; font-size: 12px;\">1<span style=\"font-size: 12px; font-family: 宋体;\">、临床检验、医学及生命科学相关专业本科及以上学历； </span></span></p><p><span style=\"font-family: Calibri; font-size: 12px;\">&nbsp;2<span style=\"font-size: 12px; font-family: 宋体;\">、有相关工作经验者优先 &nbsp;</span></span></p><p><span style=\"font-family: Calibri; font-size: 12px;\">3<span style=\"font-size: 12px; font-family: 宋体;\">、良好的人际沟通、协调和解决问题能力； </span></span></p><p><span style=\"font-family: Calibri; font-size: 12px;\">4<span style=\"font-size: 12px; font-family: 宋体;\">、熟练检索医学文献；有较好的英语阅读能力。</span></span></p><p><br/></p><p><span style=\"font-size: 12px;\">联系人：张丹</span></p><p><span style=\"font-size: 12px;\">电话：010-83682249-8042</span></p><p><span style=\"font-size: 12px;\">Email：zhangdan@innovita.com.cn</span></p>','0','','0','0','','0','1','','','');
